void add(int& x, int y) {
  x += y;
  if (x >= mod) {
    x -= mod;
  }
}

void sub(int& x, int y) {
  x -= y;
  if (x < 0) {
    x += mod;
  }
}

int mul(int x, int y) {
  return (long long) x * y % mod;
}

int tt, n;

int main() {
  ios::sync_with_stdio(false);
  cin.tie(0);
  cout.tie(0);
  cin >> tt;
  while (tt--) {
    cin >> n;
    if (n == 1) {
      cout << 0 << '\n';
    } else {
      int x = (n - 2) / 400000;
      int a = fac[x], b = s[x], c = s2[x], answer = mod - 1;
      for (int i = x * 400000; i <= n;) {
        if (i + 2 == n) {
          add(answer, c);
        }
        if (i + 1 == n) {
          sub(answer, b);
          sub(answer, b);
        }
        if (i == n) {
          add(answer, a);
        }
        ++i;
        a = mul(a, i);
        add(b, a);
        add(c, (b + 1) % mod);
      }
      cout << answer << '\n';
    }
  }
  return 0;
}
